const db = require("../database/connection");

const getAllMedia = async () => {
  const [rows] = await db.query("SELECT * FROM media");
  return rows;
};

const getMediaById = async (id) => {
  const [rows] = await db.query("SELECT * FROM media WHERE id = ?", [id]);
  return rows[0];
};

const updateMedia = async (id, data) => {
  const { title, description } = data;
  await db.query("UPDATE media SET title = ?, description = ? WHERE id = ?", [
    title,
    description,
    id,
  ]);
};

const deleteMedia = async (id) => {
  await db.query("DELETE FROM media WHERE id = ?", [id]);
};

const addMedia = async (filePath) => {
  await db.query("INSERT INTO media (file_path) VALUES (?)", [filePath]);
};

module.exports = {
  getAllMedia,
  getMediaById,
  updateMedia,
  deleteMedia,
  addMedia,
};
